    {{-- Google Location AutoComplete --}}
    <script src="https://maps.googleapis.com/maps/api/js?key={{ config('services.googleMap.key') }}&libraries=places">
    </script>

    <script>
        let autocomplete;
        let latitude;
        let longitude;

        function addressLocator() {
            const input = document.getElementById("location-autocomplete-input");

            const options = {
                componentRestrictions: {
                    country: "UK"
                },
                fields: ["address_components", "geometry", "icon", "name"],
                strictBounds: false,
            };
            // Initialize the Google Places Autocomplete
            const autocomplete = new google.maps.places.Autocomplete(input, options);
            // Listen for the "place_changed" event
            autocomplete.addListener("place_changed", function() {
                const place = autocomplete.getPlace();

                if (place.geometry && place.geometry.location) {
                    // Get latitude and longitude
                    latitude = place.geometry.location.lat();
                    longitude = place.geometry.location.lng();

                    // Update hidden fields with latitude and longitude
                    document.getElementById('longitude').value = longitude;
                    document.getElementById('latitude').value = latitude;
                } else {
                    toastr.error("Place result does not have valid geometry data.");
                    return;
                }
            });


        }
    </script>

    <script>
        document.querySelector('#location-autocomplete-input').addEventListener('input', addressLocator);
    </script>



    <script type="text/javascript">
        /* Values of x-data form */
        const propertyInformationData = {
            ownershipValue: null,
            membersValue: null,
            powerConsumptionKwhValue: null,
            powerConsumptionAmountValue: null,
            solarInstallationValue: null,
            unitElectricityDayValue: null,
            unitElectricityNightValue: null,
            locationValue: null,
            showOwnershipModal: true,
            showMembersModal: false,
            showPowerConsumptionModal: false,
            showUnitElectricityModal: false,
            showSolarInstallationModal: false,
            showSearchLocationModal: false,
            showpreviousStepButton: false,
            showContinueButton: true,
            showKwhInput: true,
            showAmountInput: false,

            ownershipStep: function() {
                if (this.ownershipValue !== 'renting' && this.ownershipValue !== null) {
                    this.showOwnershipModal = false;
                    this.showMembersModal = true;
                    this.showpreviousStepButton = true;
                } else {
                    errorHandler('ownershipErrorMessage', 'Please select an ownership option!', this
                        .ownershipValue);
                }

            },
            membersStep: function() {
                this.membersValue ? (this.showMembersModal = false, this.showPowerConsumptionModal = true) :
                    errorHandler('membersErrorMessage', 'Please select number of members option!')
            },
            powerConsumptionStep: function() {
                const selectedValue = document.querySelector('input[name="powerConsumptionInputValue"]:checked')
                    .value;
                switch (selectedValue) {
                    case "KwhValue":
                        this.powerConsumptionKwhValue && this.powerConsumptionKwhValue > 0 ? (
                            this.showPowerConsumptionModal =
                            false,
                            this.showUnitElectricityModal =
                            true) : errorHandler('powerErrorMessage', 'Please add power consumption units');

                        break;
                    case "amountValue":
                        this.powerConsumptionAmountValue && this.powerConsumptionAmountValue > 0 ? (
                            this.showPowerConsumptionModal =
                            false,
                            this.showUnitElectricityModal =
                            true) : errorHandler('powerErrorMessage', 'Please add power consumption bill');
                        break;
                    default:
                        errorHandler('powerErrorMessage', 'Please add values to proceed further');
                        break;
                }

            },
            unitElectricityStep: function() {
                const selectedValue = document.querySelector('input[name="electricityRate"]:checked').value;
                switch (selectedValue) {
                    case "rateSame":
                        this.unitElectricityDayValue && this.unitElectricityDayValue > 0 ? (this
                            .showUnitElectricityModal = false, this
                            .showSolarInstallationModal = true) : errorHandler('unitErrorMessage',
                            'Please select atleast one option to proceed!');
                        break;
                    case 'rateChanged':
                        ((this.unitElectricityDayValue && this.unitElectricityDayValue > 0) && (this
                            .unitElectricityNightValue && this.unitElectricityNightValue > 0)) ? (this
                            .showUnitElectricityModal = false, this
                            .showSolarInstallationModal = true) : errorHandler('unitErrorMessage',
                            'Please add both values to proceed!');
                        break;
                    default:
                        break;
                }
            },
            solarInstallationStep: function() {
                this.solarInstallationValue ? (
                    this.showSolarInstallationModal = false,
                    this.showSearchLocationModal = true,
                    this.showContinueButton = false) : errorHandler(
                    'installationErrorMessage',
                    'Please select installation timeframe first!')
            },


            goToNextStep: function() {
                switch (true) {
                    case (this.showOwnershipModal): //When user is on ownership Modal
                        this.ownershipStep();
                        break;
                    case (this.showMembersModal):

                        this.membersStep();
                        break;
                    case (this.showPowerConsumptionModal):

                        this.powerConsumptionStep();
                        break;
                    case (this.showUnitElectricityModal):


                        this.unitElectricityStep();
                        break;
                    case (this.showSolarInstallationModal):

                        this.solarInstallationStep();
                        break;
                }
            },
            // go to previous steps
            goToPreviousStep: function() {

                switch (true) {
                    case (this.showSearchLocationModal): //When user is on last location search step
                        this.showSolarInstallationModal = true;
                        this.showContinueButton = true;
                        this.showSearchLocationModal = false;
                        break;
                    case (this.showSolarInstallationModal): // User is on solar installation step
                        this.showUnitElectricityModal = true;
                        this.showSolarInstallationModal = false;
                        break;
                    case (this.showUnitElectricityModal): // User is on unit electricity step
                        this.showUnitElectricityModal = false;
                        this.showPowerConsumptionModal = true;
                        break;
                    case (this.showPowerConsumptionModal): // User is on power consumption step
                        this.showPowerConsumptionModal = false;
                        this.showMembersModal = true;
                        break;
                    default:
                        this.showMembersModal = false; // user is on member selection step
                        this.showOwnershipModal = true;
                        this.showpreviousStepButton = false;
                        this.showContinueButton = true;
                        break;
                }
            },



        }
    </script>

    {{-- Error Message handler  --}}
    <script type="text/javascript">
        // Push Modals to window
        window.$modals = {
            show(name, id) {
                window.dispatchEvent(
                    new CustomEvent('modal', {
                        detail: name,
                        id: id
                    })
                );
            }
        }

        function errorHandler(errorDivId, errorMessage, ownershipValue) {
            if (ownershipValue == 'renting') {
                $modals.show('serviceNotAvailable', '');
                return;
            }
            var error = document.getElementById(errorDivId);
            error.innerHTML = errorMessage;
            return;

        }


        // Exit Modal
        let exitModal = (e) => {
            const id = e.currentTarget.getAttribute('id');
            modal = id.split('-');
            if (modal.length === 2) {
                $modals.show(modal[0], id);
            }
        }
    </script>
